INSERT INTO `wp_snippets` (`id`, `name`, `description`, `code`, `tags`, `scope`, `priority`, `active`, `modified`) VALUES
(1, 'Mettre les noms des fichiers téléversés en minuscule', 'Vérifie que les images et les fichiers téléversés ont des noms en minuscule.\n\nCeci est un exemple d’extrait. N’hésitez pas à l’utiliser, à le modifier ou à le supprimer.', 'add_filter( \'sanitize_file_name\', \'mb_strtolower\' );', 'sample, media', 'global', 10, 0, '2023-01-09 16:05:07'),
(2, 'Désactiver la barre d’administration', 'Désactive la barre d’administration de WordPress pour tout le monde sauf les administrateurs/administratrices.\n\nCeci est un exemple d’extrait. N’hésitez pas à l’utiliser, à le modifier ou à le supprimer.', 'add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );', 'sample, admin-bar', 'front-end', 10, 0, '2023-01-09 16:05:07'),
(3, 'Autoriser les smileys', 'Permet la conversion des smileys dans les endroits obscurs.\n\nCeci est un exemple d’extrait. N’hésitez pas à l’utiliser, à le modifier ou à le supprimer.', 'add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );', 'sample', 'global', 10, 0, '2023-01-09 16:05:07'),
(4, 'Année en cours', 'Code court pour insérer l’année en cours dans une publication ou une page...\n\nCeci est un exemple d’extrait. N’hésitez pas à l’utiliser, à le modifier ou à le supprimer.', '<?php echo date( \'Y\' ); ?>', 'sample, dates', 'content', 10, 0, '2023-01-09 16:05:07');
